import readme from "./readme.md";
const steps = ['step 1', 'step 2', 'step 3', 'step 4', 'step 5']

export default {
  title: "feedback/Progress Indicator",
  parameters: {
    notes: readme
  },
  args: {
    activeStep: 2
  },
  argTypes: {
    activeStep: {
      name: 'active step',
      control: 'select',
      options: steps.map((step, i) => i)
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const progressIndicator = document.createElement('pn-progress-indicator');

  keys.forEach(key => progressIndicator[key] = args[key]);
  steps.forEach(step => {
    const stepEl = document.createElement('pn-progress-indicator-step');
    
    stepEl.name = step;
    progressIndicator.appendChild(stepEl);
  })

  return progressIndicator;
};

export const ProgressIndicator = Template.bind({});
