import { boolean, text } from "@storybook/addon-knobs";
import readme from "./readme.md";

export default {
  title: "feedback/Spinner",
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    light: false,
    size: '1.5'
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const spinner = document.createElement('pn-spinner');

  keys.forEach(key => spinner[key] = args[key]);

  return spinner;
};

export const Spinner = Template.bind({});

