import readme from "./readme.md";
    
export default {
  title: "feedback/Tooltip",
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    warning: false,
    color: 'blue'
  },
  argTypes: {
    color: {
      control: 'select',
      options: ['blue', 'warning', 'white'],
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const tooltip = document.createElement('pn-tooltip');
  const titleEl = document.createElement('h3');
  const textEl = document.createElement('p');

  titleEl.innerText = 'Title';
  textEl.innerText = 'Text, text, text';

  keys.forEach(key => tooltip[key] = args[key]);
  tooltip.appendChild(titleEl);
  tooltip.appendChild(textEl);

  return tooltip;
};

export const Tooltip = Template.bind({});
