import readme from "./readme.md";

export default {
  title: 'Input/Checkbox',
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    checkboxid: 'example-id',
    value: 'example-value',
    name: 'example-name',
    disabled: false,
    checked: false,
    indeterminate: false,
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const checkbox = document.createElement('pn-checkbox');

  keys.forEach(key => checkbox[key] = args[key]);

  return checkbox;
};

export const Checkbox = Template.bind({});
