import readme from "./readme.md";

export default {
  title: "input/Choice chip",
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    checked: false,
    indeterminate: false,
    choiceid: 'example-id',
    value: 'example-value',
    name: 'example-name',
    label: 'label'
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const choiceChip = document.createElement('pn-choice-chip');
  
  keys.forEach(key => choiceChip[key] = args[key]);
  choiceChip.innerText = args.label;

  return choiceChip;
};

export const ChoiceChip = Template.bind({});
