import readme from './readme.md'

export default {
  title: "input/File Upload",
  parameters: {
    notes: readme
  },
  args: {
    disabled: false,
    maxSize: '4MB',
    showSpinner: false,
    accept: '.doc.txt',
    limit: 2
  },
  argTypes: {
     maxSize: {
       name: 'max size',
       type: 'text',
     },
     showSpinner: {
       name: 'show spinner'
     }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const fileUpload = document.createElement('pn-file-upload');

  keys.forEach(key => fileUpload[key] = args[key]);

  return fileUpload;
};

export const FileUpload = Template.bind({});
