import readme from './readme.md';

export default {
  title: "input/Input",
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    type: 'text',
    placeholder: 'Placeholder example',
    disabled: false,
    helpertext: '* helper text to explain what the expected input is',
    name: 'example-name',
    label: 'Label'
  },
  argTypes: {
    type: {
      control: 'select',
      options: ['text', 'password', 'url', 'tel', 'search', 'number', 'email'],
    },
    error: {
      control: 'text'
    },
    autocomplete: {
      control: 'text'
    },
    maxlength: {
      name: 'max-length',
      control: 'number'
    },
    required: {
      control: 'boolean'
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const input = document.createElement('pn-input');

  keys.forEach(key => input[key] = args[key]);

  return input;
};

export const Input = Template.bind({});
