import readme from "./readme.md";

export default {
  title: 'input/Radio Button',
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    radioid: 'example-id',
    name: 'example-name',
    value: 'example-value',
    checked: false,
    disabled: false
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const container = document.createElement('div');
  const radioButton = document.createElement('pn-radio-button');
  const label = document.createElement('label');

  container.classList.add('radio-button-cont');
  label.setAttribute('for', args.radioid);
  label.innerText = 'example label';

  keys.forEach(key => radioButton[key] = args[key]);
  container.appendChild(radioButton);
  container.appendChild(label);

  return container;
};

export const RadioButton = Template.bind({});
