import readme from "./readme.md";

export default {
  title: 'input/Search Field',
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    inputid: 'example-id',
    label: 'search',
    placeholder: 'Example placeholder',
    disabled: false,
    name: 'example-name',
    light: false
  },
  argTypes: {
    button: {
      control: 'select',
      options: ['', 'none', 'icon', 'icon-inline']
    },
    value: {
      control: 'text'
    },
    list: {
      control: 'text'
    },
    autocomplete: {
      control: 'text'
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const searchField = document.createElement('pn-search-field');

  keys.forEach(key => searchField[key] = args[key]);

  return searchField;
};

export const SearchField = Template.bind({});
