import readme from "./readme.md";
const options = [
  'One',
  'Two',
  'Three',
  'Four',
  'Five',
];


export default {
  title: 'input/Select',
  parameters: {
    notes: readme
  },
  args: {
    label: 'Description of choice',
    placeholder: 'Select an option',
    searchPlaceholder: 'Search',
    searchable: false,
    checkbox: false,
    checkboxid: 'example-id',
    invalid: false
  },
  argTypes: {
    error: {
      control: 'text'
    },
    searchPlaceholder: {
      name: 'search-placeholder'
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const select = document.createElement('pn-select');

  keys.forEach(key => select[key] = args[key]);
  options.forEach(option => {
    const optionEl = document.createElement('pn-option');
    optionEl.value = option;
    optionEl.innerText = option;
    optionEl.checkbox = args.checkbox;
    select.appendChild(optionEl);
  });

  return select;
};

export const Select = Template.bind({});
