import readme from "./readme.md";

export default {
  title: 'input/Toggle Switch',
  parameters: {
    notes: readme
  },
  args: {
    toggleid: 'example-id',
    name: 'example-name',
    checked: false,
    indeterminate: false,
    disabled: false
  },
  argTypes: {
    toggleid: {
      name: 'toggleid (REQUIRED)'
    },
    indeterminate: {
      name: 'indeterminate (loading state)'
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const toggleSwitch = document.createElement('pn-toggle-switch');

  keys.forEach(key => toggleSwitch[key] = args[key]);

  return toggleSwitch;
};

export const ToggleSwitch = Template.bind({});
