import readme from "./readme.md";

export default {
  title: 'navigation/Menu',
  parameters: {
    notes: readme
  },
  args: {
    disabled: false,
    label: 'text'
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const menu = document.createElement('pn-menu');

  keys.forEach(key => menu[key] = args[key]);

  return menu;
};

export const Menu = Template.bind({});
