import readme from "./readme.md";

export default {
  title: "web components/Generate Franko-license OCR",
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    language: 'sv'
  },
  argTypes: {
    language: {
      control: 'select',
      options: ['en', 'sv', 'da', 'fi', 'no']
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const ocrSearch = document.createElement('pn-ocr-search');

  keys.forEach(key => ocrSearch[key] = args[key]);

  return ocrSearch;
};

export const OcrSearch = Template.bind({});

