import { text, boolean, select } from "@storybook/addon-knobs";
import readme from "./readme.md";

export default {
  title: "Web Components/Virtual Agent",
  parameters: {
    notes: readme,
    layout: 'centered'
  },
  args: {
    areaid: '23',
    show: true,
    parcelid: '96110810953SE'
  },
  argTypes: {
    areaid: {
      control: 'select',
      options: ['23', '29', '30']
    }
  }
};

const Template = (args) => {
  const keys = Object.keys(args);
  const virtualAgent = document.createElement('pn-virtual-agent');

  keys.forEach(key => virtualAgent[key] = args[key]);

  return virtualAgent;
};

export const VirtualAgent = Template.bind({});

