export declare class PnCheckbox {
  /** This will be emitted on change and input, like a native checkbox */
  value: any;
  /** The name of the checkbox group */
  name: string;
  /** The string you put here will be what you'd make the ID of a native radio button, you can't name props "id" because it's reserved
   * so we went with the more descriptive name "checkboxid", if you want to create labels for your checkbox, you will bind it to this ID
   */
  checkboxid: string;
  disabled?: boolean;
  checked?: boolean;
  indeterminate?: boolean;
  ariaLabel: string;
  ariaDescribedby: string;
  hostElement: HTMLElement;
  handlechange(): void;
  componentDidLoad(): void;
  componentDidUpdate(): void;
  checkAndSetIndeterminateState(): void;
  render(): any;
}
